static auto _ = []() { std::ios::sync_with_stdio(false); std::cin.tie(NULL); return 0; }();
vector<vector<vector<int>>> dp(500,vector<vector<int>>(500,vector<int>(4,0)));
class Solution {
public:
    int orderOfLargestPlusSign(int N, vector<vector<int>>& mines) {
        set<int> mineSet;
        for(vector<int> v : mines){
            mineSet.insert(v[0]*N+v[1]);
        }
        vector<int> results(N*N,N*N);
        // 0.up 1.left 2.down 3.rioght
        dp[0][0][0] = dp[0][0][1] = mineSet.count(0) ? 0 : 1;
        results[0] = min(dp[0][0][0], dp[0][0][1]);
        for(int i=1;i<N;++i){
            dp[i][0][0] = mineSet.count(i*N) ? 0 : 1 + dp[i-1][0][0];
            dp[i][0][1] = mineSet.count(i*N) ? 0 : 1;
            results[i*N] = min(results[i*N], min(dp[i][0][0], dp[i][0][1]));
            
            
            dp[0][i][0] = mineSet.count(i) ? 0 : 1;
            dp[0][i][1] = mineSet.count(i) ? 0 : 1 + dp[0][i-1][1];
            results[i] = min(results[i], min(dp[0][i][1], dp[0][i][0]));
        }
        
        // up,left
        int raw = 0;
        for(int i=1;i<N;++i){
            for(int j=1;j<N;++j){
                raw = i*N+j;
                dp[i][j][0] = mineSet.count(raw) ? 0 : dp[i-1][j][0] + 1;
                dp[i][j][1] = mineSet.count(raw) ? 0 : dp[i][j-1][1] + 1;
                results[raw] = min(results[raw], min(dp[i][j][0], dp[i][j][1]));
            }
        }
        
        dp[N-1][N-1][2] = dp[N-1][N-1][3] = mineSet.count(N*N-1) ? 0 : 1;
        results[N*N - 1] = min(dp[N-1][N-1][2], dp[N-1][N-1][3]);
        for(int i=N-2;i>-1;--i){
            dp[i][N-1][2] = mineSet.count(N*i + N-1) ? 0 : dp[i+1][N-1][2] + 1;
            dp[i][N-1][3] = mineSet.count(N*i + N-1) ? 0 : 1;
            results[i*N+N-1] = min(results[i*N+N-1], min(dp[i][N-1][2], dp[i][N-1][3]));
            
            dp[N-1][i][2] = mineSet.count(N*(N-1)+i) ? 0 : 1;
            dp[N-1][i][3] = mineSet.count(N*(N-1)+i) ? 0 : dp[N-1][i+1][3] + 1;
            results[N*(N-1)+i] = min(results[N*(N-1)+i], min(dp[N-1][i][2], dp[N-1][i][3]));
        }
        
        for(int i=N-2;i>-1;--i){
            for(int j=N-2;j>-1;--j){
                raw = i*N+j;
                dp[i][j][2] = mineSet.count(raw) ? 0 : dp[i+1][j][2] + 1;
                dp[i][j][3] = mineSet.count(raw) ? 0 : dp[i][j+1][3] + 1;
                results[raw] = min(results[raw], min(dp[i][j][2], dp[i][j][3]));
            }
        }
        
        return results[max_element(results.begin(), results.end()) - results.begin()];
    }
};


